import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function StudentLogin() {
  const [form, setForm] = useState({ roll: "", dob: "" });
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/students/login", form);
      alert(res.data.message);
      navigate("/student/dashboard");
    } catch {
      alert("Invalid Roll or DOB");
    }
  };

  return (
    <div className="container">
      <h2>Student Login</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input name="roll" placeholder="Roll No (e.g. 22671)" onChange={handleChange} required />
        <input name="dob" type="date" onChange={handleChange} required />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

const styles = {
  form: { display: "flex", flexDirection: "column", gap: "10px", width: "300px", margin: "auto" },
};

export default StudentLogin;
