import React, { useState } from "react";
import AddStudent from "./AddStudent";
import UploadNotes from "./UploadNotes";
import ManageNotes from "./ManageNotes";

function AdminDashboard() {
  const [tab, setTab] = useState("addStudent");

  const renderTab = () => {
    switch (tab) {
      case "addStudent":
        return <AddStudent />;
      case "uploadNotes":
        return <UploadNotes />;
      case "manageNotes":
        return <ManageNotes />;
      default:
        return <AddStudent />;
    }
  };

  return (
    <div className="container">
      <h2>Admin Dashboard</h2>

      <div style={styles.tabs}>
        <button
          onClick={() => setTab("addStudent")}
          style={tab === "addStudent" ? styles.active : styles.btn}
        >
          Add Student
        </button>
        <button
          onClick={() => setTab("uploadNotes")}
          style={tab === "uploadNotes" ? styles.active : styles.btn}
        >
          Upload Notes
        </button>
        <button
          onClick={() => setTab("manageNotes")}
          style={tab === "manageNotes" ? styles.active : styles.btn}
        >
          Manage Notes
        </button>
      </div>

      <div style={{ marginTop: "20px" }}>{renderTab()}</div>
    </div>
  );
}

const styles = {
  tabs: {
    display: "flex",
    gap: "10px",
    justifyContent: "center",
    marginBottom: "20px",
  },
  btn: {
    background: "#ccc",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  active: {
    background: "#007bff",
    color: "#fff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default AdminDashboard;
