import React, { useState, useEffect } from "react";
import ViewNotes from "./ViewNotes.jsx";
import { FiMenu, FiX } from "react-icons/fi";

function StudentDashboard() {
  const studentData = JSON.parse(localStorage.getItem("studentData"));
  const [menuOpen, setMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  const year = studentData.year;
  const semester = studentData.semester;

  // Convert semester number to Roman numeral
  const semesterRoman = (sem) => {
    const roman = ["I", "II", "III", "IV", "V", "VI", "VII", "VIII"];
    return roman[sem - 1] || sem;
  };

  // Detect mobile screen
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("studentData");
    window.location.href = "/";
  };

  // Internal CSS
  const styles = {
    container: {
      minHeight: "100vh",
      backgroundColor: "#f3f4f6",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    },
    navbar: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      backgroundColor: "#16a34a",
      color: "#fff",
      padding: "16px 24px",
    },
    navLinks: {
      display: "flex",
      alignItems: "center",
      gap: "16px",
    },
    button: {
      backgroundColor: "#fff",
      color: "#16a34a",
      border: "none",
      padding: "8px 16px",
      borderRadius: "8px",
      cursor: "pointer",
      fontWeight: "600",
      transition: "background-color 0.3s",
    },
    mobileMenu: {
      backgroundColor: "#22c55e",
      color: "#fff",
      padding: "16px 24px",
      display: "flex",
      flexDirection: "column",
      gap: "8px",
    },
    card: {
      backgroundColor: "#fff",
      padding: "24px",
      borderRadius: "24px",
      boxShadow: "0 8px 16px rgba(0,0,0,0.15)",
      maxWidth: "500px",
      margin: "24px auto",
    },
    cardTitle: {
      fontSize: "20px",
      fontWeight: "700",
      marginBottom: "12px",
      color: "#111827",
    },
    text: {
      fontSize: "16px",
      color: "#374151",
      marginBottom: "8px",
    },
    infoRow: {
      display: "flex",
      justifyContent: "space-between",
      gap: "12px",
    },
    infoBox: {
      flex: 1,
      padding: "12px",
      borderRadius: "16px",
      border: "1px solid #d1d5db",
      backgroundColor: "#f9fafb",
      textAlign: "center",
      fontWeight: "600",
    },
    viewNotesContainer: {
      maxWidth: "900px",
      margin: "32px auto",
      padding: "0 16px",
    },
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <nav style={styles.navbar}>
        <h1>Student Dashboard</h1>
        {isMobile ? (
          <button
            onClick={() => setMenuOpen(!menuOpen)}
            style={{ background: "none", border: "none", color: "#fff", cursor: "pointer" }}
          >
            {menuOpen ? <FiX size={24} /> : <FiMenu size={24} />}
          </button>
        ) : (
          <div style={styles.navLinks}>
            <span>Welcome, {studentData.name}</span>
            <button style={styles.button} onClick={handleLogout}>
              Logout
            </button>
          </div>
        )}
      </nav>

      {/* Mobile menu */}
      {menuOpen && isMobile && (
        <div style={styles.mobileMenu}>
          <span>Welcome, {studentData.name}</span>
          <button style={styles.button} onClick={handleLogout}>
            Logout
          </button>
        </div>
      )}

      {/* Student Info Card */}
      <div style={styles.card}>
        <h2 style={styles.cardTitle}>Student Information</h2>
        <p style={styles.text}>
          <strong>Roll No:</strong> {studentData.rollNo}
        </p>
        <p style={styles.text}>
          <strong>Department:</strong> {studentData.department}
        </p>

        <div style={styles.infoRow}>
          <div style={styles.infoBox}>
            Year
            <div>{year}</div>
          </div>
          <div style={styles.infoBox}>
            Semester
            <div>{semesterRoman(semester)}</div>
          </div>
        </div>
      </div>

      {/* View Notes */}
      <div style={styles.viewNotesContainer}>
        <ViewNotes department={studentData.department} year={year} semester={semester} />
      </div>
    </div>
  );
}

export default StudentDashboard;
