import React, { useState } from "react";
import axios from "axios";

function UploadNotes() {
  const [form, setForm] = useState({
    title: "",
    fileUrl: "",
    year: 1,
    semester: 1,
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  // Semester options in Roman numerals
  const semesterOptions = [
    { value: 1, label: "I" },
    { value: 2, label: "II" },
    { value: 3, label: "III" },
    { value: 4, label: "IV" },
    { value: 5, label: "V" },
    { value: 6, label: "VI" },
    { value: 7, label: "VII" },
    { value: 8, label: "VIII" },
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: name === "year" || name === "semester" ? Number(value) : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Get admin department from localStorage
      const adminData = JSON.parse(localStorage.getItem("adminData"));
      const department = adminData.department;

      await axios.post("/api/admin/upload-notes", {
        ...form,
        department,
      });

      setMessage("Note uploaded successfully!");
      setError("");
      setForm({ title: "", fileUrl: "", year: 1, semester: 1 });
    } catch (err) {
      setError(err.response?.data?.message || "Upload failed");
      setMessage("");
    }
  };

  // Internal CSS
  const styles = {
    container: { maxWidth: "500px", margin: "40px auto", fontFamily: "'Segoe UI', sans-serif" },
    input: { width: "100%", padding: "12px", marginBottom: "12px", borderRadius: "8px", border: "1px solid #d1d5db" },
    button: { padding: "12px", borderRadius: "8px", border: "none", backgroundColor: "#16a34a", color: "#fff", cursor: "pointer", fontWeight: "600" },
    label: { fontWeight: "600", marginBottom: "4px", display: "block" },
    message: { color: "green", fontWeight: "600", marginBottom: "12px" },
    error: { color: "red", fontWeight: "600", marginBottom: "12px" },
  };

  return (
    <div style={styles.container}>
      <h2>Upload Notes</h2>
      {message && <div style={styles.message}>{message}</div>}
      {error && <div style={styles.error}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Title</label>
        <input type="text" name="title" value={form.title} onChange={handleChange} style={styles.input} required />

        <label style={styles.label}>File URL (Cloudinary)</label>
        <input type="text" name="fileUrl" value={form.fileUrl} onChange={handleChange} style={styles.input} required />

        <label style={styles.label}>Year</label>
        <input type="number" name="year" value={form.year} onChange={handleChange} min="1" max="4" style={styles.input} required />

        <label style={styles.label}>Semester</label>
        <select name="semester" value={form.semester} onChange={handleChange} style={styles.input} required>
          {semesterOptions.map((sem) => (
            <option key={sem.value} value={sem.value}>{sem.label}</option>
          ))}
        </select>

        <button type="submit" style={styles.button}>Upload Note</button>
      </form>
    </div>
  );
}

export default UploadNotes;
