import React, { useState } from "react";
import axios from "axios";

function AddStudent() {
  const [form, setForm] = useState({
    roll: "",
    name: "",
    dob: "",
    year: "",
    semester: "",
  });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/students/add", form);
      alert(res.data.message);
    } catch (err) {
      alert("Error adding student");
    }
  };

  return (
    <div className="container">
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input name="roll" placeholder="Roll No" onChange={handleChange} required />
        <input name="name" placeholder="Name" onChange={handleChange} required />
        <input name="dob" type="date" onChange={handleChange} required />
        <select name="year" onChange={handleChange} required>
          <option value="">Select Year</option>
          <option>I Year</option>
          <option>II Year</option>
          <option>III Year</option>
          <option>IV Year</option>
        </select>
        <select name="semester" onChange={handleChange} required>
          <option value="">Select Semester</option>
          <option>I</option>
          <option>II</option>
          <option>III</option>
          <option>IV</option>
          <option>V</option>
          <option>VI</option>
          <option>VII</option>
          <option>VIII</option>
        </select>
        <button type="submit">Add Student</button>
      </form>
    </div>
  );
}

const styles = {
  form: { display: "flex", flexDirection: "column", gap: "10px", width: "300px", margin: "auto" },
};

export default AddStudent;
