import React, { useState, useEffect } from "react";
import ViewNotes from "./ViewNotes.jsx";
import { FiMenu, FiX, FiUser, FiBook, FiLayers } from "react-icons/fi";

function StudentDashboard() {
  const studentData = JSON.parse(localStorage.getItem("studentData"));
  const [year, setYear] = useState(studentData.year);
  const [semester, setSemester] = useState(studentData.semester);
  const [menuOpen, setMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("studentData");
    window.location.href = "/";
  };

  const styles = {
    container: {
      minHeight: "100vh",
      background: "linear-gradient(120deg, #ecfdf5, #d1fae5, #f0fdfa)",
      fontFamily: "'Poppins', sans-serif",
      paddingBottom: "40px",
      transition: "background 0.4s ease-in-out",
    },
    navbar: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      background: "linear-gradient(90deg, #15803d, #16a34a, #22c55e)",
      color: "#fff",
      padding: "18px 28px",
      boxShadow: "0 4px 10px rgba(0,0,0,0.15)",
      position: "sticky",
      top: 0,
      zIndex: 100,
    },
    navLinks: { display: "flex", alignItems: "center", gap: "20px", fontSize: "17px" },
    button: {
      background: "linear-gradient(90deg, #22c55e, #16a34a)",
      color: "#fff",
      border: "none",
      padding: "10px 22px",
      borderRadius: "14px",
      cursor: "pointer",
      fontWeight: "600",
      letterSpacing: "0.5px",
      boxShadow: "0 3px 8px rgba(34,197,94,0.3)",
      transition: "transform 0.2s, box-shadow 0.3s",
    },
    logoutButton: {
      background: "linear-gradient(90deg, #ef4444, #dc2626)",
      color: "#fff",
      border: "none",
      padding: "10px 22px",
      borderRadius: "14px",
      cursor: "pointer",
      fontWeight: "600",
      letterSpacing: "0.5px",
      boxShadow: "0 3px 8px rgba(239,68,68,0.3)",
      transition: "transform 0.2s, box-shadow 0.3s",
    },
    mobileMenu: {
      backgroundColor: "#22c55e",
      color: "#fff",
      padding: "16px 24px",
      display: "flex",
      flexDirection: "column",
      gap: "14px",
      animation: "fadeIn 0.3s ease",
    },
    card: {
      backgroundColor: "#fff",
      padding: "36px",
      borderRadius: "28px",
      boxShadow: "0 12px 30px rgba(0,0,0,0.12)",
      maxWidth: "560px",
      margin: "40px auto",
      transition: "transform 0.3s, box-shadow 0.3s",
    },
    cardTitle: {
      fontSize: "26px",
      fontWeight: "700",
      marginBottom: "18px",
      color: "#111827",
      borderBottom: "3px solid #16a34a",
      paddingBottom: "10px",
      display: "flex",
      alignItems: "center",
      gap: "10px",
    },
    text: {
      fontSize: "17px",
      color: "#374151",
      marginBottom: "12px",
      display: "flex",
      alignItems: "center",
      gap: "10px",
    },
    inputContainer: {
      display: "flex",
      gap: "14px",
      flexWrap: "wrap",
      marginTop: "18px",
    },
    input: {
      flex: "1",
      padding: "10px 16px",
      borderRadius: "12px",
      border: "1px solid #d1d5db",
      outline: "none",
      fontSize: "15px",
      transition: "border-color 0.3s, box-shadow 0.3s",
    },
    viewNotesContainer: {
      maxWidth: "1000px",
      margin: "40px auto",
      padding: "0 16px",
      display: "grid",
      gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
      gap: "24px",
    },
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <nav style={styles.navbar}>
        <h1 style={{ fontSize: "22px", fontWeight: "700" }}>Student Dashboard</h1>

        {isMobile ? (
          <button
            onClick={() => setMenuOpen(!menuOpen)}
            style={{ background: "none", border: "none", color: "#fff", cursor: "pointer" }}
          >
            {menuOpen ? <FiX size={26} /> : <FiMenu size={26} />}
          </button>
        ) : (
          <div style={styles.navLinks}>
            <span>Welcome, {studentData.name}</span>
            <button
              style={styles.logoutButton}
              onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
              onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        )}
      </nav>

      {menuOpen && isMobile && (
        <div style={styles.mobileMenu}>
          <span>Welcome, {studentData.name}</span>
          <button
            style={styles.logoutButton}
            onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      )}

      {/* Student Info Card */}
      <div
        style={styles.card}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = "translateY(-6px)";
          e.currentTarget.style.boxShadow = "0 20px 40px rgba(0,0,0,0.15)";
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = "translateY(0)";
          e.currentTarget.style.boxShadow = "0 12px 24px rgba(0,0,0,0.12)";
        }}
      >
        <h2 style={styles.cardTitle}>
          <FiUser /> Student Information
        </h2>
        <p style={styles.text}>
          <FiBook /> <strong>Roll No:</strong> {studentData.rollNo}
        </p>
        <p style={styles.text}>
          <FiLayers /> <strong>Department:</strong> {studentData.department}
        </p>
        <div style={styles.inputContainer}>
          <input
            type="number"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            placeholder="Year"
            style={styles.input}
            onFocus={(e) => (e.target.style.boxShadow = "0 0 6px rgba(22, 163, 74, 0.3)")}
            onBlur={(e) => (e.target.style.boxShadow = "none")}
          />
          <input
            type="number"
            value={semester}
            onChange={(e) => setSemester(e.target.value)}
            placeholder="Semester"
            style={styles.input}
            onFocus={(e) => (e.target.style.boxShadow = "0 0 6px rgba(22, 163, 74, 0.3)")}
            onBlur={(e) => (e.target.style.boxShadow = "none")}
          />
        </div>
      </div>

      {/* View Notes */}
      <div style={styles.viewNotesContainer}>
        <ViewNotes department={studentData.department} year={year} semester={semester} />
      </div>
    </div>
  );
}

export default StudentDashboard;
