import React, { useState, useEffect } from "react";
import ViewNotes from "./ViewNotes.jsx";
import { FiMenu, FiX, FiUser, FiBook, FiLayers, FiPlus } from "react-icons/fi";

function StudentDashboard() {
  const studentData = JSON.parse(localStorage.getItem("studentData"));
  const [year, setYear] = useState(studentData.year);
  const [semester, setSemester] = useState(studentData.semester);
  const [menuOpen, setMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("studentData");
    window.location.href = "/";
  };

  const styles = {
    container: {
      minHeight: "100vh",
      background: "linear-gradient(120deg, #f0fdfa, #d1fae5)",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      paddingBottom: "60px",
    },
    navbar: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      backgroundColor: "#16a34a",
      color: "#fff",
      padding: "16px 24px",
      boxShadow: "0 4px 6px rgba(0,0,0,0.1)",
      position: "sticky",
      top: 0,
      zIndex: 100,
    },
    navLinks: { display: "flex", alignItems: "center", gap: "16px", fontSize: "16px" },
    button: {
      background: "linear-gradient(90deg, #22c55e, #16a34a)",
      color: "#fff",
      border: "none",
      padding: "10px 20px",
      borderRadius: "12px",
      cursor: "pointer",
      fontWeight: "600",
      transition: "transform 0.2s, box-shadow 0.2s",
    },
    mobileMenu: {
      backgroundColor: "#22c55e",
      color: "#fff",
      padding: "16px 24px",
      display: "flex",
      flexDirection: "column",
      gap: "12px",
    },
    card: {
      backgroundColor: "#fff",
      padding: "32px",
      borderRadius: "24px",
      boxShadow: "0 12px 24px rgba(0,0,0,0.12)",
      maxWidth: "550px",
      margin: "32px auto",
      transition: "transform 0.3s, box-shadow 0.3s",
      position: "relative",
      overflow: "hidden",
    },
    avatar: {
      width: "60px",
      height: "60px",
      borderRadius: "50%",
      backgroundColor: "#16a34a",
      color: "#fff",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: "28px",
      fontWeight: "700",
      marginBottom: "16px",
      boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
    },
    cardTitle: {
      fontSize: "24px",
      fontWeight: "700",
      marginBottom: "16px",
      color: "#111827",
      borderBottom: "2px solid #16a34a",
      paddingBottom: "8px",
      display: "flex",
      alignItems: "center",
      gap: "8px",
    },
    text: {
      fontSize: "16px",
      color: "#374151",
      marginBottom: "12px",
      display: "flex",
      alignItems: "center",
      gap: "8px",
    },
    inputContainer: { display: "flex", gap: "12px", flexWrap: "wrap", marginTop: "16px" },
    input: {
      flex: "1",
      padding: "10px 16px",
      borderRadius: "12px",
      border: "1px solid #d1d5db",
      outline: "none",
      transition: "border-color 0.3s, box-shadow 0.3s",
    },
    viewNotesContainer: {
      maxWidth: "1000px",
      margin: "40px auto",
      padding: "0 16px",
      display: "grid",
      gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
      gap: "24px",
    },
    noteCard: {
      backgroundColor: "#fff",
      padding: "20px",
      borderRadius: "20px",
      boxShadow: "0 6px 18px rgba(0,0,0,0.1)",
      transition: "transform 0.3s, box-shadow 0.3s, background 0.3s",
      cursor: "pointer",
    },
    fab: {
      position: "fixed",
      bottom: "30px",
      right: "30px",
      width: "60px",
      height: "60px",
      borderRadius: "50%",
      background: "linear-gradient(90deg, #22c55e, #16a34a)",
      color: "#fff",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: "28px",
      boxShadow: "0 6px 20px rgba(0,0,0,0.3)",
      cursor: "pointer",
      transition: "transform 0.2s, box-shadow 0.2s",
    },
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <nav style={styles.navbar}>
        <h1 style={{ fontSize: "22px", fontWeight: "700" }}>Student Dashboard</h1>

        {isMobile ? (
          <button
            onClick={() => setMenuOpen(!menuOpen)}
            style={{ background: "none", border: "none", color: "#fff", cursor: "pointer" }}
          >
            {menuOpen ? <FiX size={26} /> : <FiMenu size={26} />}
          </button>
        ) : (
          <div style={styles.navLinks}>
            <span>Welcome, {studentData.name}</span>
            <button style={styles.button} onClick={handleLogout}>
              Logout
            </button>
          </div>
        )}
      </nav>

      {menuOpen && isMobile && (
        <div style={styles.mobileMenu}>
          <span>Welcome, {studentData.name}</span>
          <button style={styles.button} onClick={handleLogout}>
            Logout
          </button>
        </div>
      )}

      {/* Student Info Card */}
      <div
        style={styles.card}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = "translateY(-6px)";
          e.currentTarget.style.boxShadow = "0 20px 40px rgba(0,0,0,0.15)";
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = "translateY(0)";
          e.currentTarget.style.boxShadow = "0 12px 24px rgba(0,0,0,0.12)";
        }}
      >
        <div style={styles.avatar}>{studentData.name[0]}</div>
        <h2 style={styles.cardTitle}>
          <FiUser /> Student Information
        </h2>
        <p style={styles.text}>
          <FiBook /> <strong>Roll No:</strong> {studentData.rollNo}
        </p>
        <p style={styles.text}>
          <FiLayers /> <strong>Department:</strong> {studentData.department}
        </p>
        <div style={styles.inputContainer}>
          <input
            type="number"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            placeholder="Year"
            style={styles.input}
            onFocus={(e) => (e.target.style.boxShadow = "0 0 6px rgba(22, 163, 74, 0.3)")}
            onBlur={(e) => (e.target.style.boxShadow = "none")}
          />
          <input
            type="number"
            value={semester}
            onChange={(e) => setSemester(e.target.value)}
            placeholder="Semester"
            style={styles.input}
            onFocus={(e) => (e.target.style.boxShadow = "0 0 6px rgba(22, 163, 74, 0.3)")}
            onBlur={(e) => (e.target.style.boxShadow = "none")}
          />
        </div>
      </div>

      {/* View Notes */}
      <div style={styles.viewNotesContainer}>
        <ViewNotes department={studentData.department} year={year} semester={semester} />
      </div>

      {/* Floating Action Button */}
      <div
        style={styles.fab}
        onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.1)")}
        onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
      >
        <FiPlus />
      </div>
    </div>
  );
}

export default StudentDashboard;
