import React from "react";
import { Link } from "react-router-dom";

function Home() {
  return (
    <div className="container" style={styles.container}>
      <h1 style={styles.title}>ðŸ“š Smart Note Organizer</h1>
      <p style={styles.subtitle}>Login to manage and access your notes efficiently</p>

      <div style={styles.buttons}>
        <Link to="/admin/login" style={styles.adminBtn}>Admin Login</Link>
        <Link to="/student/login" style={styles.studentBtn}>Student Login</Link>
      </div>
    </div>
  );
}

const styles = {
  container: { textAlign: "center", marginTop: "80px" },
  title: { fontSize: "2.5rem", color: "#222" },
  subtitle: { color: "#555" },
  buttons: { marginTop: "40px", display: "flex", justifyContent: "center", gap: "20px" },
  adminBtn: { background: "#007bff", color: "#fff", padding: "12px 20px", borderRadius: "5px" },
  studentBtn: { background: "#28a745", color: "#fff", padding: "12px 20px", borderRadius: "5px" },
};

export default Home;
