import React, { useState } from "react";
import axios from "axios";

function UploadNotes() {
  const [form, setForm] = useState({ year: "", semester: "" });
  const [file, setFile] = useState(null);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleUpload = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("file", file);
    data.append("year", form.year);
    data.append("semester", form.semester);

    try {
      const res = await axios.post("http://localhost:5000/api/notes/upload", data);
      alert(res.data.message);
    } catch {
      alert("Upload failed");
    }
  };

  return (
    <div className="container">
      <h2>Upload Notes</h2>
      <form onSubmit={handleUpload} style={styles.form}>
        <select name="year" onChange={handleChange} required>
          <option value="">Select Year</option>
          <option>I Year</option>
          <option>II Year</option>
          <option>III Year</option>
          <option>IV Year</option>
        </select>
        <select name="semester" onChange={handleChange} required>
          <option value="">Select Semester</option>
          <option>I</option>
          <option>II</option>
          <option>III</option>
          <option>IV</option>
          <option>V</option>
          <option>VI</option>
          <option>VII</option>
          <option>VIII</option>
        </select>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}

const styles = {
  form: { display: "flex", flexDirection: "column", gap: "10px", width: "300px", margin: "auto" },
};

export default UploadNotes;
