import React, { useEffect, useState } from "react";
import axios from "axios";

function ManageNotes() {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/notes/all").then((res) => setNotes(res.data));
  }, []);

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/notes/${id}`);
    setNotes(notes.filter((n) => n._id !== id));
  };

  return (
    <div className="container">
      <h2>Manage Notes</h2>
      {notes.map((n) => (
        <div key={n._id} style={styles.noteBox}>
          <p><b>{n.year}</b> - Semester {n.semester}</p>
          <a href={n.fileUrl} target="_blank" rel="noreferrer">View / Download</a>
          <button onClick={() => handleDelete(n._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

const styles = {
  noteBox: {
    border: "1px solid #ccc",
    padding: "10px",
    margin: "10px 0",
    borderRadius: "5px",
  },
};

export default ManageNotes;
