import React, { useState } from "react";
import axios from "axios";

function ViewNotes() {
  const [form, setForm] = useState({ year: "", semester: "" });
  const [notes, setNotes] = useState([]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSearch = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://localhost:5000/api/notes/view", form);
    setNotes(res.data);
  };

  return (
    <div className="container">
      <h2>View Notes</h2>
      <form onSubmit={handleSearch} style={styles.form}>
        <select name="year" onChange={handleChange} required>
          <option value="">Select Year</option>
          <option>I Year</option>
          <option>II Year</option>
          <option>III Year</option>
          <option>IV Year</option>
        </select>
        <select name="semester" onChange={handleChange} required>
          <option value="">Select Semester</option>
          <option>I</option>
          <option>II</option>
          <option>III</option>
          <option>IV</option>
          <option>V</option>
          <option>VI</option>
          <option>VII</option>
          <option>VIII</option>
        </select>
        <button type="submit">Search</button>
      </form>

      <div>
        {notes.length ? (
          notes.map((n) => (
            <div key={n._id} style={styles.noteBox}>
              <p>{n.year} - Sem {n.semester}</p>
              <a href={n.fileUrl} target="_blank" rel="noreferrer">Download</a>
            </div>
          ))
        ) : (
          <p>No notes found. You can request admin to upload.</p>
        )}
      </div>
    </div>
  );
}

const styles = {
  form: { display: "flex", flexDirection: "column", gap: "10px", width: "300px", margin: "auto" },
  noteBox: { border: "1px solid #ccc", padding: "10px", marginTop: "10px" },
};

export default ViewNotes;
