import React, { useState, useEffect } from "react";
import axios from "axios";

function ViewNotes({ department, year, semester }) {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    if (!department || !year || !semester) return;

    const fetchNotes = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/notes`, {
          params: { department, year, semester }
        });
        setNotes(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchNotes();
  }, [department, year, semester]);

  return (
    <div>
      <h2>Notes for {department} Year {year}, Semester {semester}</h2>
      {notes.length === 0 ? (
        <p>No notes uploaded yet.</p>
      ) : (
        <ul>
          {notes.map((note) => (
            <li key={note._id}>
              <a href={note.fileUrl} target="_blank" rel="noreferrer">{note.title}</a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ViewNotes;
